// src/services/api.js
import axiosInstance from '../axiosConfig';

export const clienteAPI = {
    // Crear nueva solicitud (usado por el comercial)
    async crearSolicitud(datosCliente) {
        const response = await axiosInstance.post('/api/solicitudes/', datosCliente, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        });
        return response.data;
    },

    // Obtener estado de una solicitud
    async obtenerEstadoSolicitud(solicitudId) {
        const response = await axiosInstance.get(`/api/solicitudes/${solicitudId}`, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        });
        return response.data;
    },

    // Actualizar estado de una solicitud (usado por director, pedidos y admin)
    async actualizarEstadoSolicitud(solicitudId, datos) {
        const response = await axiosInstance.put(`/api/solicitudes/${solicitudId}/aprobar`, datos, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        });
        return response.data;
    },

    // Obtener solicitudes pendientes según rol
    async obtenerSolicitudesPendientes(rol) {
        const response = await axiosInstance.get(`/api/solicitudes/pendientes/${rol}`, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        });
        return response.data;
    },

    // Obtener resumen de solicitudes
    async obtenerResumenSolicitudes() {
        const response = await axiosInstance.get('/api/solicitudes/resumen', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        });
        return response.data;
    },

    // Nueva función para el login
    async login(username, password) {
        const response = await axiosInstance.post('/token', new URLSearchParams({
            username,
            password
        }), {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            }
        });
        return response.data;
    }
};
