// En src/components/ClienteForm.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ClienteForm = () => {
    const [formData, setFormData] = useState({
        nombre: '',
        direccion: '',
        poblacion: '',
        codigoPostal: '',
        direccionEnvio: '',
        poblacionEnvio: '',
        codigoPostalEnvio: '',
        nombreContacto: '',
        telefono: '',
        correo: '',
        cif_nif: '',
        tipoCarga: 'COMP',
        metodoPago: 'TRANSFERENCIA',
        solicitudCredito: 0,
        esAutonomo: false,
    });
    const [sepaFile, setSepaFile] = useState(null);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [sameAddress, setSameAddress] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (type === 'checkbox') {
            setFormData({
                ...formData,
                [name]: checked
            });
        } else {
            setFormData({
                ...formData,
                [name]: value
            });
        }
    };

    const handleFileChange = (e) => {
        if (e.target.files && e.target.files.length > 0) {
            setSepaFile(e.target.files[0]);
        }
    };

    const handleSameAddressChange = (e) => {
        const checked = e.target.checked;
        setSameAddress(checked);
        if (checked) {
            setFormData({
                ...formData,
                direccionEnvio: formData.direccion,
                poblacionEnvio: formData.poblacion,
                codigoPostalEnvio: formData.codigoPostal
            });
        } else {
            setFormData({
                ...formData,
                direccionEnvio: '',
                poblacionEnvio: '',
                codigoPostalEnvio: ''
            });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setError(null);

        // Crear un FormData para enviar los datos y el archivo
        const formDataToSend = new FormData();
        
        // Añadir todos los campos del formulario
        Object.keys(formData).forEach(key => {
            // Convertir booleanos a string para enviarlos correctamente
            if (typeof formData[key] === 'boolean') {
                formDataToSend.append(key, formData[key].toString());
            } else {
                formDataToSend.append(key, formData[key]);
            }
        });
        
        // Añadir el archivo SEPA si existe
        if (sepaFile) {
            formDataToSend.append('sepa', sepaFile);
        }
        
        // Debug para ver qué se está enviando
        console.log('Enviando datos:');
        for (let pair of formDataToSend.entries()) {
            console.log(pair[0] + ': ' + pair[1]);
        }

        try {
            // Enviar directamente con fetch para tener más control
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8000/api/solicitudes/', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`
                    // NO incluir 'Content-Type': 'application/json' - FormData lo establece automáticamente
                },
                body: formDataToSend
            });
            
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(JSON.stringify(errorData.detail || 'Error al crear la solicitud'));
            }
            
            const result = await response.json();
            alert('Solicitud creada con éxito');
            navigate('/dashboard');
        } catch (err) {
            console.error('Error al crear solicitud:', err);
            setError(err.message || 'Error al crear la solicitud');
        } finally {
            setIsLoading(false);
        }
    };

    const handleCancel = () => {
        navigate('/dashboard');
    };

    return (
        <div className="max-w-3xl mx-auto p-6 bg-white shadow-md rounded-lg">
            <h2 className="text-2xl font-bold mb-6 text-gray-800">Alta de Cliente</h2>
            <form onSubmit={handleSubmit} className="space-y-8">
                {error && (
                    <div className="rounded-md bg-red-50 p-4">
                        <div className="flex">
                            <div className="ml-3">
                                <h3 className="text-sm font-medium text-red-800">
                                    Error al crear la solicitud: {error}
                                </h3>
                            </div>
                        </div>
                    </div>
                )}

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                        <label htmlFor="nombre" className="block text-sm font-medium text-gray-700">
                            Nombre del Cliente *
                        </label>
                        <div className="mt-1">
                            <input
                                id="nombre"
                                name="nombre"
                                type="text"
                                required
                                value={formData.nombre}
                                onChange={handleChange}
                                className="appearance-none block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                            />
                        </div>
                    </div>

                    <div>
                        <label htmlFor="cif_nif" className="block text-sm font-medium text-gray-700">
                            CIF/NIF *
                        </label>
                        <div className="mt-1">
                            <input
                                id="cif_nif"
                                name="cif_nif"
                                type="text"
                                required
                                value={formData.cif_nif}
                                onChange={handleChange}
                                className="appearance-none block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                            />
                        </div>
                    </div>
                </div>

                {/* Resto del formulario... (mantén todos los campos igual) */}

                {/* Para el input file, modifica para usar handleFileChange */}
                {(formData.metodoPago === 'RECIBO' || formData.metodoPago === 'RECIBO B2B') && (
                    <div>
                        <label htmlFor="sepa" className="block text-sm font-medium text-gray-700">
                            Documento SEPA (Obligatorio)
                        </label>
                        <div className="mt-1">
                            <input
                                id="sepa"
                                name="sepa"
                                type="file"
                                onChange={handleFileChange}
                                className="appearance-none block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                            />
                        </div>
                    </div>
                )}

                <div>
                    <label htmlFor="esAutonomo" className="block text-sm font-medium text-gray-700">
                        ¿Es Autónomo? *
                    </label>
                    <div className="mt-1">
                        <input
                            id="esAutonomo"
                            name="esAutonomo"
                            type="checkbox"
                            checked={formData.esAutonomo}
                            onChange={handleChange}
                            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                        />
                    </div>
                </div>

                <div className="flex justify-end space-x-4">
                    <button
                        type="button"
                        onClick={handleCancel}
                        className="py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                        Cancelar
                    </button>
                    <button
                        type="submit"
                        disabled={isLoading}
                        className={`py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white ${isLoading ? 'bg-blue-400' : 'bg-blue-600 hover:bg-blue-700'} focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500`}
                    >
                        {isLoading ? (
                            <span className="flex items-center">
                                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"></path>
                                </svg>
                                Enviando...
                            </span>
                        ) : (
                            'Enviar Solicitud'
                        )}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default ClienteForm;
