import React, { useState } from 'react';
import Layout from './Layout';
import { useAuth } from '../context/AuthContext';
import { Card, CardHeader, CardTitle, CardContent } from './ui/card';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Select } from './ui/select';
import { Alert, AlertDescription } from './ui/alert';
import { Upload, Copy, CheckCircle } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { Checkbox } from './ui/checkbox';

const ClienteForm = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    nombre: '',
    direccionFacturacion: '',
    poblacionFacturacion: '',
    codigoPostalFacturacion: '',
    usarMismaDireccion: true,
    direccionEnvio: '',
    poblacionEnvio: '',
    codigoPostalEnvio: '',
    nombreContacto: '',
    telefono: '',
    correo: '',
    cif_nif: '',
    tipoCarga: '',
    metodoPago: '',
    solicitudCredito: '',
    esAutonomo: false,
    documentos: {
      sepa: null
    }
  });

  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const validateComercialForm = () => {
    const newErrors = {};
    const requiredFields = [
      'nombre', 'direccionFacturacion', 'poblacionFacturacion', 'codigoPostalFacturacion',
      'nombreContacto', 'telefono', 'correo', 'cif_nif', 'tipoCarga', 'metodoPago'
    ];

    requiredFields.forEach(field => {
      if (!formData[field]) newErrors[field] = 'Este campo es obligatorio';
    });

    if (!formData.usarMismaDireccion) {
      if (!formData.direccionEnvio) newErrors.direccionEnvio = 'Este campo es obligatorio';
      if (!formData.poblacionEnvio) newErrors.poblacionEnvio = 'Este campo es obligatorio';
      if (!formData.codigoPostalEnvio) newErrors.codigoPostalEnvio = 'Este campo es obligatorio';
    }

    // Validación para SEPA si el método de pago es RECIBO o RECIBO B2B
    if ((formData.metodoPago === 'RECIBO' || formData.metodoPago === 'RECIBO B2B') && !formData.documentos.sepa) {
      newErrors.sepa = 'El documento SEPA es obligatorio para este método de pago';
    }

    if (formData.solicitudCredito && isNaN(Number(formData.solicitudCredito))) {
      newErrors.solicitudCredito = 'La cantidad debe ser un número válido';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
  };

  const handleDocumentoChange = (tipo, file) => {
    setFormData(prev => ({
      ...prev,
      documentos: { ...prev.documentos, [tipo]: file }
    }));
    if (errors[tipo]) {
      setErrors(prev => ({ ...prev, [tipo]: null }));
    }
  };

  const handleUsarMismaDireccion = (checked) => {
    setFormData(prev => ({
      ...prev,
      usarMismaDireccion: checked,
      direccionEnvio: checked ? prev.direccionFacturacion : prev.direccionEnvio,
      poblacionEnvio: checked ? prev.poblacionFacturacion : prev.poblacionEnvio,
      codigoPostalEnvio: checked ? prev.codigoPostalFacturacion : prev.codigoPostalEnvio
    }));
  };

  const handleCopiarDireccion = () => {
    setFormData(prev => ({
      ...prev,
      direccionEnvio: prev.direccionFacturacion,
      poblacionEnvio: prev.poblacionFacturacion,
      codigoPostalEnvio: prev.codigoPostalFacturacion
    }));
  };

  const handleSubmit = async () => {
    if (!validateComercialForm()) return;

    setIsLoading(true);
    try {
      const uploadedDocs = {};
      
      // Solo subir documento SEPA si es necesario
      if (formData.documentos.sepa && (formData.metodoPago === 'RECIBO' || formData.metodoPago === 'RECIBO B2B')) {
        const formDataFile = new FormData();
        formDataFile.append('file', formData.documentos.sepa);
        const response = await fetch('http://localhost:8000/api/upload/sepa', {
          method: 'POST',
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` },
          body: formDataFile
        });
        if (!response.ok) throw new Error('Error al subir documento SEPA');
        const data = await response.json();
        uploadedDocs.sepa = data.url;
      }

      const datosParaEnviar = {
        nombre: formData.nombre,
        direccion: formData.direccionFacturacion,
        poblacion: formData.poblacionFacturacion,
        codigoPostal: formData.codigoPostalFacturacion,
        direccionEnvio: formData.usarMismaDireccion ? formData.direccionFacturacion : formData.direccionEnvio,
        poblacionEnvio: formData.usarMismaDireccion ? formData.poblacionFacturacion : formData.poblacionEnvio,
        codigoPostalEnvio: formData.usarMismaDireccion ? formData.codigoPostalFacturacion : formData.codigoPostalEnvio,
        nombreContacto: formData.nombreContacto,
        telefono: formData.telefono,
        correo: formData.correo,
        cif_nif: formData.cif_nif,
        tipoCarga: formData.tipoCarga,
        metodoPago: formData.metodoPago,
        solicitudCredito: formData.solicitudCredito.trim() ? Number(formData.solicitudCredito) : 0,
        esAutonomo: formData.esAutonomo,
        documentos: uploadedDocs
      };

      const response = await fetch('http://localhost:8000/api/solicitudes/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(datosParaEnviar)
      });

      if (!response.ok) throw new Error('Error al crear la solicitud');
      const data = await response.json();
      console.log('Solicitud creada:', data);
      setCurrentStep(prev => prev + 1);
    } catch (error) {
      console.error('Error:', error);
      setErrors(prev => ({ ...prev, submit: 'Error al crear la solicitud: ' + error.message }));
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Layout>
      <div className="container mx-auto px-4 py-8">
        <Card className="max-w-4xl mx-auto">
          <CardHeader>
            <CardTitle className="text-2xl font-bold">Sistema de Alta de Nuevos Clientes</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="mb-8">
              <div className="flex justify-between items-center mb-8">
                {[1, 2, 3, 4].map(step => (
                  <div key={step} className={`w-12 h-12 rounded-full flex items-center justify-center ${currentStep === step ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-600'}`}>
                    {step}
                  </div>
                ))}
              </div>
              <h2 className="text-xl font-semibold mb-4">Alta de Nuevo Cliente - Paso {currentStep} de 4</h2>
            </div>

            {currentStep === 1 && (
              <form className="space-y-6" onSubmit={e => e.preventDefault()}>
                {/* Datos básicos */}
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Datos del Cliente</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                      <Label htmlFor="nombre">Nombre / Razón Social *</Label>
                      <Input id="nombre" value={formData.nombre} onChange={e => handleChange('nombre', e.target.value)} className={errors.nombre ? 'border-red-500' : ''} />
                      {errors.nombre && <span className="text-sm text-red-500">{errors.nombre}</span>}
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="cif_nif">CIF/NIF *</Label>
                      <Input id="cif_nif" value={formData.cif_nif} onChange={e => handleChange('cif_nif', e.target.value)} className={errors.cif_nif ? 'border-red-500' : ''} />
                      {errors.cif_nif && <span className="text-sm text-red-500">{errors.cif_nif}</span>}
                    </div>
                    <div className="flex items-center space-x-2">
                      <Checkbox id="esAutonomo" checked={formData.esAutonomo} onCheckedChange={checked => handleChange('esAutonomo', checked)} />
                      <Label htmlFor="esAutonomo">¿Es autónomo?</Label>
                    </div>
                  </div>
                </div>

                {/* Dirección de Facturación */}
                <div className="space-y-4 border-t pt-4">
                  <h3 className="text-lg font-semibold">Dirección de Facturación</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2 md:col-span-2">
                      <Label htmlFor="direccionFacturacion">Dirección de Facturación *</Label>
                      <Input id="direccionFacturacion" value={formData.direccionFacturacion} onChange={e => handleChange('direccionFacturacion', e.target.value)} className={errors.direccionFacturacion ? 'border-red-500' : ''} />
                      {errors.direccionFacturacion && <span className="text-sm text-red-500">{errors.direccionFacturacion}</span>}
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="poblacionFacturacion">Población *</Label>
                      <Input id="poblacionFacturacion" value={formData.poblacionFacturacion} onChange={e => handleChange('poblacionFacturacion', e.target.value)} className={errors.poblacionFacturacion ? 'border-red-500' : ''} />
                      {errors.poblacionFacturacion && <span className="text-sm text-red-500">{errors.poblacionFacturacion}</span>}
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="codigoPostalFacturacion">Código Postal *</Label>
                      <Input id="codigoPostalFacturacion" value={formData.codigoPostalFacturacion} onChange={e => handleChange('codigoPostalFacturacion', e.target.value)} className={errors.codigoPostalFacturacion ? 'border-red-500' : ''} />
                      {errors.codigoPostalFacturacion && <span className="text-sm text-red-500">{errors.codigoPostalFacturacion}</span>}
                    </div>
                  </div>
                </div>

                {/* Dirección de Envío */}
                <div className="space-y-4 border-t pt-4">
                  <div className="flex justify-between items-center">
                    <h3 className="text-lg font-semibold">Dirección de Envío</h3>
                    <div className="flex items-center space-x-2">
                      <Checkbox id="misma-direccion" checked={formData.usarMismaDireccion} onCheckedChange={handleUsarMismaDireccion} />
                      <Label htmlFor="misma-direccion" className="text-sm cursor-pointer">Misma que facturación</Label>
                    </div>
                  </div>
                  {!formData.usarMismaDireccion && (
                    <>
                      <div className="flex justify-end">
                        <Button type="button" variant="outline" size="sm" onClick={handleCopiarDireccion} className="text-xs">
                          <Copy className="mr-1 h-3 w-3" /> Copiar de facturación
                        </Button>
                      </div>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-2 md:col-span-2">
                          <Label htmlFor="direccionEnvio">Dirección de Envío *</Label>
                          <Input id="direccionEnvio" value={formData.direccionEnvio} onChange={e => handleChange('direccionEnvio', e.target.value)} className={errors.direccionEnvio ? 'border-red-500' : ''} disabled={formData.usarMismaDireccion} />
                          {errors.direccionEnvio && <span className="text-sm text-red-500">{errors.direccionEnvio}</span>}
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor="poblacionEnvio">Población *</Label>
                          <Input id="poblacionEnvio" value={formData.poblacionEnvio} onChange={e => handleChange('poblacionEnvio', e.target.value)} className={errors.poblacionEnvio ? 'border-red-500' : ''} disabled={formData.usarMismaDireccion} />
                          {errors.poblacionEnvio && <span className="text-sm text-red-500">{errors.poblacionEnvio}</span>}
                        </div>
                        <div className="space-y-2">
                          <Label htmlFor="codigoPostalEnvio">Código Postal *</Label>
                          <Input id="codigoPostalEnvio" value={formData.codigoPostalEnvio} onChange={e => handleChange('codigoPostalEnvio', e.target.value)} className={errors.codigoPostalEnvio ? 'border-red-500' : ''} disabled={formData.usarMismaDireccion} />
                          {errors.codigoPostalEnvio && <span className="text-sm text-red-500">{errors.codigoPostalEnvio}</span>}
                        </div>
                      </div>
                    </>
                  )}
                </div>

                {/* Datos de contacto y comerciales */}
                <div className="space-y-4 border-t pt-4">
                  <h3 className="text-lg font-semibold">Datos de Contacto y Comerciales</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                      <Label htmlFor="nombreContacto">Nombre de Contacto *</Label>
                      <Input id="nombreContacto" value={formData.nombreContacto} onChange={e => handleChange('nombreContacto', e.target.value)} className={errors.nombreContacto ? 'border-red-500' : ''} />
                      {errors.nombreContacto && <span className="text-sm text-red-500">{errors.nombreContacto}</span>}
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="telefono">Teléfono *</Label>
                      <Input id="telefono" value={formData.telefono} onChange={e => handleChange('telefono', e.target.value)} className={errors.telefono ? 'border-red-500' : ''} />
                      {errors.telefono && <span className="text-sm text-red-500">{errors.telefono}</span>}
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="correo">Correo Electrónico *</Label>
                      <Input id="correo" type="email" value={formData.correo} onChange={e => handleChange('correo', e.target.value)} className={errors.correo ? 'border-red-500' : ''} />
                      {errors.correo && <span className="text-sm text-red-500">{errors.correo}</span>}
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="tipoCarga">Tipo de Carga *</Label>
                      <Select id="tipoCarga" value={formData.tipoCarga} onChange={e => handleChange('tipoCarga', e.target.value)} className={errors.tipoCarga ? 'border-red-500' : ''}>
                        <option value="">Seleccione tipo de carga</option>
                        <option value="COMP">COMP</option>
                        <option value="CROSS">CROSS</option>
                        <option value="GRUP">GRUP</option>
                        <option value="TTPRO">TTPRO</option>
                      </Select>
                      {errors.tipoCarga && <span className="text-sm text-red-500">{errors.tipoCarga}</span>}
                    </div>
                  </div>
                </div>

                {/* Información de pago */}
                <div className="space-y-4 border-t pt-4">
                  <h3 className="text-lg font-semibold">Información de Pago</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                      <Label htmlFor="metodoPago">Método de Pago *</Label>
                      <Select id="metodoPago" value={formData.metodoPago} onChange={e => handleChange('metodoPago', e.target.value)} className={errors.metodoPago ? 'border-red-500' : ''}>
                        <option value="">Seleccione método de pago</option>
                        <option value="TRANSFERENCIA">TRANSFERENCIA</option>
                        <option value="RECIBO">RECIBO</option>
                        <option value="RECIBO B2B">RECIBO B2B</option>
                        <option value="CONF. CLIENTE">CONF. CLIENTE</option>
                        <option value="CREDITO">CRÉDITO</option>
                      </Select>
                      {errors.metodoPago && <span className="text-sm text-red-500">{errors.metodoPago}</span>}
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="solicitudCredito">Solicitud de crédito (€)</Label>
                      <Input id="solicitudCredito" type="number" placeholder="Cantidad" value={formData.solicitudCredito} onChange={e => handleChange('solicitudCredito', e.target.value)} className={errors.solicitudCredito ? 'border-red-500' : ''} />
                      {errors.solicitudCredito && <span className="text-sm text-red-500">{errors.solicitudCredito}</span>}
                    </div>
                  </div>
                </div>

                {/* SEPA documento (solo para RECIBO o RECIBO B2B) */}
                {(formData.metodoPago === 'RECIBO' || formData.metodoPago === 'RECIBO B2B') && (
                  <div className="space-y-4 border-t pt-4">
                    <h3 className="text-lg font-semibold">Documentación SEPA</h3>
                    <div className="space-y-2">
                      <Label>Documento SEPA *</Label>
                      <Button type="button" variant="outline" onClick={() => document.getElementById('sepa-upload').click()}>
                        <Upload className="mr-2 h-4 w-4" /> Subir SEPA
                      </Button>
                      <input id="sepa-upload" type="file" className="hidden" onChange={e => handleDocumentoChange('sepa', e.target.files[0])} />
                      {formData.documentos.sepa && (
                        <div className="flex items-center mt-2">
                          <CheckCircle className="h-5 w-5 text-green-500 mr-2" />
                          <span className="text-sm text-gray-600">{formData.documentos.sepa.name}</span>
                        </div>
                      )}
                      {errors.sepa && <span className="text-sm text-red-500 block">{errors.sepa}</span>}
                    </div>
                  </div>
                )}

                {errors.submit && (
                  <Alert variant="destructive">
                    <AlertDescription>{errors.submit}</AlertDescription>
                  </Alert>
                )}

                <div className="flex justify-end mt-8">
                  <Button onClick={handleSubmit} disabled={isLoading}>
                    {isLoading ? 'Enviando...' : 'Enviar Solicitud'}
                  </Button>
                </div>
              </form>
            )}

            {currentStep === 2 && (
              <Alert>
                <AlertDescription>
                  Solicitud enviada correctamente. Esperando validación del Director Comercial.
                  <div className="mt-4">
                    <Button onClick={() => navigate('/mis-solicitudes')}>Ver Mis Solicitudes</Button>
                  </div>
                </AlertDescription>
              </Alert>
            )}
          </CardContent>
        </Card>
      </div>
    </Layout>
  );
};

export default ClienteForm;
