                    }
                />
                <Route
                    path="/solicitudes-pendientes/:rol"
                    element={
                        <ProtectedRoute>
                            <>
                                <NavBarContent />
                                <AprobacionSolicitudes />
                            </>
                        </ProtectedRoute>
                    }
                />
                <Route
                    path="/manage-users"
                    element={
                        <ProtectedRoute>
                            <>
