// src/App.jsx
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useNavigate } from 'react-router-dom';
import Login from './pages/LoginPage';
import Dashboard from './pages/Dashboard';
import MisSolicitudes from './pages/MisSolicitudes';
import AprobacionSolicitudes from './components/AprobacionSolicitudes';
import ManageUsers from './pages/ManageUsers';
import PerfilUsuario from './pages/PerfilUsuario';
import ClienteForm from './components/ClienteForm';
import { clienteAPI } from './services/api';
import { useAuth } from './context/AuthContext'; // Asegúrate de importar useAuth

const App = () => {
    const [user, setUser] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    
    console.log("Renderizando App, estado inicial:", { user, isLoading });

    // Obtener el perfil del usuario al cargar la aplicación
    useEffect(() => {
        const fetchUser = async () => {
            try {
                // Verificar si hay un usuario en localStorage
                const savedUser = localStorage.getItem('user');
                if (savedUser) {
                    const parsedUser = JSON.parse(savedUser);
                    console.log("Usuario cargado desde localStorage:", parsedUser);
                    console.log("Propiedades del usuario:", Object.keys(parsedUser));
                    console.log("¿Tiene propiedad 'rol'?", 'rol' in parsedUser);
                    console.log("¿Tiene propiedad 'role'?", 'role' in parsedUser);
                    setUser(parsedUser);
                } else {
                    // Si no hay método getUserProfile, podemos usar el token para obtener datos del usuario
                    const token = localStorage.getItem('token');
                    if (token) {
                        console.log("Token encontrado pero no hay usuario guardado");
                        // Intentar obtener los datos del usuario desde la API
                        try {
                            const response = await clienteAPI.getUserProfile();
                            console.log("Datos del usuario obtenidos desde API:", response.data);
                            const userData = {
                                email: response.data.email,
                                rol: response.data.rol,
                                name: response.data.nombre_completo,
                                is_temporary_password: response.data.is_temporary_password
                            };
                            localStorage.setItem('user', JSON.stringify(userData));
                            setUser(userData);
                        } catch (apiError) {
                            console.error("Error al obtener perfil del usuario:", apiError);
                        }
                    }
                }
            } catch (err) {
                console.error("Error al cargar usuario:", err);
                localStorage.removeItem('user');
            } finally {
                setIsLoading(false);
            }
        };
        fetchUser();
    }, []);

    // Componente de ruta protegida
    const ProtectedRoute = ({ children, requiredRole }) => {
        console.log("ProtectedRoute - Comprobando acceso:", { 
            user, 
            requiredRole,
            userRol: user?.rol,
            isTemporaryPassword: user?.is_temporary_password,
            isLoading,
            childrenType: children?.type?.name || "unknown"
        });
        
        if (isLoading) {
            return <p className="text-gray-600">Cargando...</p>;
        }
        if (!user) {
            console.log("ProtectedRoute - No hay usuario, redirigiendo a login");
            return <Navigate to="/login" />;
        }
        if (requiredRole && user.rol !== requiredRole) {
            console.log(`ProtectedRoute - Usuario no tiene rol requerido (${requiredRole}), redirigiendo a dashboard`);
            return <Navigate to="/dashboard" />;
        }
        // Si el usuario tiene una contraseña temporal, redirigir al perfil
        if (user.is_temporary_password && children.type !== PerfilUsuario) {
            console.log("ProtectedRoute - Usuario tiene contraseña temporal, redirigiendo a perfil");
            return <Navigate to="/perfil" />;
        }
        console.log("ProtectedRoute - Acceso permitido");
        return children;
    };

    // Componente de navegación - Modificado para usar useAuth
    const NavBarContent = () => {
        const navigate = useNavigate();
        // Usa directamente useAuth para obtener el usuario actual del contexto
        const { user: authUser, logout } = useAuth();
        
        console.log("NavBarContent - Usuario desde context:", authUser);
        console.log("NavBarContent - Usuario desde props:", user);
        
        // Botón de depuración
        const debugUser = () => {
            console.log("DEBUG - Usuario del contexto:", authUser);
            console.log("DEBUG - Usuario del estado:", user);
            console.log("DEBUG - Usuario en localStorage:", JSON.parse(localStorage.getItem('user')));
            console.log("DEBUG - Token en localStorage:", localStorage.getItem('token'));
        };
        
        const handleLogout = () => {
            console.log("Cerrando sesión...");
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            setUser(null);
            if (logout) {
                logout(); // Usa el logout del contexto si está disponible
            }
            navigate('/login');
        };

        // Usar authUser (del contexto) si está disponible, de lo contrario usar user (del estado de App)
        const currentUser = authUser || user;

        return (
            <nav className="bg-gray-800 p-4">
                <div className="max-w-7xl mx-auto flex justify-between items-center">
                    <div className="flex items-center">
                        <img src="/svan-logo.png" alt="SVAN Logo" className="h-8 mr-4" />
                        <Link to="/dashboard" className="text-white hover:text-gray-300 mr-4">Dashboard</Link>
                        <Link to="/mis-solicitudes" className="text-white hover:text-gray-300 mr-4">Mis Solicitudes</Link>
                        
                        {/* Debug de propiedades */}
                        <div className="text-xs text-gray-400 mr-4">
                            {currentUser ? `Rol: ${currentUser.rol}` : 'No user'}
                        </div>
                        
                        {currentUser && currentUser.rol === 'admin' && (
                            <>
                                <Link to="/solicitudes-pendientes/admin" className="text-white hover:text-gray-300 mr-4">
                                    Solicitudes Pendientes de Administración
                                </Link>
                                <Link to="/manage-users" className="text-white hover:text-gray-300 mr-4">
                                    Gestionar Usuarios
                                </Link>
                            </>
                        )}
                        {currentUser && currentUser.rol === 'director' && (
                            <Link to="/solicitudes-pendientes/director" className="text-white hover:text-gray-300 mr-4">
                                Solicitudes Pendientes de Dirección
                            </Link>
                        )}
                        {currentUser && currentUser.rol === 'pedidos' && (
                            <Link to="/solicitudes-pendientes/pedidos" className="text-white hover:text-gray-300 mr-4">
                                Solicitudes Pendientes de Pedidos
                            </Link>
                        )}
                        {currentUser && currentUser.rol === 'comercial' && (
                            <Link to="/nuevo-cliente" className="text-white hover:text-gray-300 mr-4">
                                Nueva Solicitud
                            </Link>
                        )}
                    </div>
                    <div className="flex items-center">
                        {/* Botón de depuración */}
                        <button 
                            onClick={debugUser} 
                            className="text-white bg-purple-600 px-2 py-1 rounded mr-2"
                        >
                            Debug User
                        </button>
                        
                        <Link to="/perfil" className="text-white hover:text-gray-300 mr-4">Perfil</Link>
                        <button
                            onClick={handleLogout}
                            className="text-white hover:text-gray-300"
                        >
                            Cerrar Sesión
                        </button>
                    </div>
                </div>
            </nav>
        );
    };

    // Componente para depurar rutas
    const RouteDebugger = () => {
        console.log("Rutas disponibles:");
        console.log("- /dashboard - Dashboard principal");
        console.log("- /mis-solicitudes - Ver mis solicitudes");
        console.log("- /solicitudes-pendientes/:rol - Solicitudes pendientes por rol");
        console.log("- /manage-users - Gestión de usuarios (requiere rol 'admin')");
        console.log("- /perfil - Perfil de usuario");
        console.log("- /nuevo-cliente - Formulario de nuevo cliente (requiere rol 'comercial')");
        
        return null;
    };

    return (
        <Router>
            <RouteDebugger />
            <Routes>
                <Route path="/login" element={<Login setUser={setUser} />} />
                <Route
                    path="/dashboard"
                    element={
                        <ProtectedRoute>
                            <>
                                <NavBarContent />
                                <Dashboard />
                            </>
                        </ProtectedRoute>
                    }
                />
                <Route
                    path="/mis-solicitudes"
                    element={
                        <ProtectedRoute>
                            <>
                                <NavBarContent />
                                <MisSolicitudes />
                            </>
                        </ProtectedRoute>
                    }
                />
                <Route
                    path="/solicitudes-pendientes/:rol"
                    element={
                        <ProtectedRoute>
                            <>
                                <NavBarContent />
                                <AprobacionSolicitudes />
                            </>
                        </ProtectedRoute>
                    }
                />
                <Route
                    path="/manage-users"
                    element={
                        <ProtectedRoute requiredRole="admin">
                            <>
                                <NavBarContent />
                                <ManageUsers />
                            </>
                        </ProtectedRoute>
                    }
                />
                <Route
                    path="/perfil"
                    element={
                        <ProtectedRoute>
                            <>
                                <NavBarContent />
                                <PerfilUsuario />
                            </>
                        </ProtectedRoute>
                    }
                />
                <Route
                    path="/nuevo-cliente"
                    element={
                        <ProtectedRoute requiredRole="comercial">
                            <>
                                <NavBarContent />
                                <ClienteForm />
                            </>
                        </ProtectedRoute>
                    }
                />
                <Route path="/" element={<Navigate to="/dashboard" />} />
                <Route path="*" element={<Navigate to="/dashboard" />} />
            </Routes>
        </Router>
    );
};

export default App;
